import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Modal, useModal } from '../components';
import './screens.css';
import ProductImg from '../assets/product.png';

interface Corridor {
  from: string;
  to: string;
}

interface QuickSendContact {
  name: string;
  amount: string;
  currency: string;
}

